sys_script_include.script:
function RecordMaintenance(grRecords)
{
	var RecordBulkProcessor = x_snc_devtools.RecordBulkProcessor;
	var RecordGetWhitelistHash = x_snc_datasilos.RecordGetWhitelistHash;
	var RecordGetGhostValue = x_snc_datasilos.RecordGetGhostValue;

	var strTable = grRecords.getRecordClassName();

	function CallbackRecordProcessor(record)
	{
		var strWhitelistHashOld = RecordGetWhitelistHash(record);

		var bGhost = RecordGetGhostValue(record);
		//if (bGhost == false)
		{
			RecordUpdate(record);
		}

		var strWhitelistHashNew = RecordGetWhitelistHash(record);
		if (strWhitelistHashOld != strWhitelistHashNew)
		{
			return true;
		}
		return false; 
	}
	function CallbackLog(strText)
	{
		Log('RecordMaintenance: ['+strTable+']: '+strText);
	}


	RecordBulkProcessor(
		grRecords,
		CallbackRecordProcessor, // callback function to work on a single record
		'x_snc_datasilosvam_processed', // the name of the field that stores that date/time of the last processing
		100000, // process up to 100K records
		60*9, // run for 9 minutes
		CallbackLog, // Callback function used for logging
		1000 // Produce a status log output every 1000 records
		);

}
